import React, { useEffect, useState } from "react";

import { Route, Switch, Redirect } from "react-router-dom";
import MovieList from "./components/MovieList";
import Movie from "./components/Movie";

import MovieHeader from "./components/MovieHeader";

import FavoriteMovieList from "./components/FavoriteMovieList";
import EditMovieForm from "./components/EditMovieForm";

import axios from "axios";
import AddMovieForm from "./components/AddMovieForm";
import useAxios from "./hooks/useAxios";

const App = (props) => {
  //const [movies, setMovies] = useState([]);
  const [favoriteMovies, setFavoriteMovies] = useState([]);
  const [movies, getMovies] = useAxios([]);

  useEffect(() => {
    axios
      .get("http://localhost:9000/api/movies")
      .then((res) => {
        setMovies(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const deleteMovie = (id) => {
    const remainingMovie = movies.filter((movie) => movie.id === id);
    setMovies(remainingMovie);

    const remainingFavoriteMovie = favoriteMovies.filter(
      (movie) => movie.id === id
    );
    setFavoriteMovies(remainingFavoriteMovie);
  };

  const addToFavorites = (movie) => {
    const newFavoriteMovies = [...favoriteMovies, movie];
    setFavoriteMovies(newFavoriteMovies);
  };

  return (
    <div>
      <nav className="bg-zinc-800 px-6 py-3">
        <h1 className="text-xl text-white">HTTP / CRUD Film Projesi</h1>
      </nav>

      <div className="max-w-4xl mx-auto px-3 pb-4">
        <MovieHeader />
        <div className="flex flex-col sm:flex-row gap-4">
          <FavoriteMovieList favoriteMovies={favoriteMovies} />

          <Switch>
            <Route path="/movies/edit/:id">
              {" "}
              <EditMovieForm setMovies={setMovies} />
            </Route>
            <Route path="/movies/add">
              {" "}
              <AddMovieForm setMovies={setMovies} />
            </Route>

            <Route path="/movies/:id">
              <Movie
                deleteMovie={deleteMovie}
                addToFavorites={addToFavorites}
              />
            </Route>

            <Route path="/movies">
              <MovieList movies={movies} />
            </Route>

            <Route path="/">
              <Redirect to="/movies" />
            </Route>
          </Switch>
        </div>
      </div>
    </div>
  );
};

export default App;
